import { View } from "native-base";
import React from "react";
import { Text } from "react-native";

export default function Category({ _category }) {
  return (
    <View w={"40%"} bg={_category.color} p={5} my={2} borderRadius={"xl"}>
      <Text>{_category.title}</Text>
    </View>
  );
}
