import { View, Text, Center, Pressable } from "native-base";
import React from "react";
import { useWindowDimensions } from "react-native";
import { Platform } from "react-native";
export default function CategoryGridItem({ _category }) {
  const { width, height } = useWindowDimensions();

  return (
    <Center
      w={width > height ? "30%" : "45%"}
      bg={_category.color}
      borderRadius={"xl"}
      py={16}
      mb={8}
      shadow="9"
      // overflow={Platform.OS === "android" ? "hidden" : "visible"}
    >
      <Pressable android_ripple={"#ccc"} bg="amber.100">
        <Text fontWeight={"700"} fontSize={16} italic>
          {_category.title}
        </Text>
      </Pressable>
    </Center>
  );
}
