import { View, Text, Center } from "native-base";
import React from "react";

export default function Category({ _category }) {
  return (
    <Center w={"40%"} bg={_category.color} py={20} my={2} borderRadius={"xl"}>
      <Text>{_category.title}</Text>
    </Center>
  );
}
