import { View, Text, Center } from "native-base";
import React from "react";
import { useWindowDimensions } from "react-native";

export default function Category({ _category }) {
  const { width, height } = useWindowDimensions();
  return (
    <Center w={"45%"} bg={_category.color} py={16} my={3} borderRadius={"xl"}>
      <Text fontWeight={"700"} fontSize={16} italic>
        {_category.title}
      </Text>
    </Center>
  );
}
